package com.wq.controller;import com.wq.entity.User;import org.springframework.web.bind.annotation.*;/** * Created by wengqian on 2018/6/15. */@RestControllerpublic class HelloController {    @RequestMapping(value = "/hello")    public String hello( @RequestParam String name) {        return "hello "+name+"，this is first messge(micro-service1)";    }    @RequestMapping(value = "/hello1")    public String hello( @RequestBody User user) {        return "hello "+user.getName()+"，this is first messge(micro-service1)";    }}